parameters.functionScript:
if(Inputs.FirstName === undefined || Inputs.LastName === undefined) return;

var initials = Inputs.FirstName[0] + Inputs.LastName[0];

Component.Object.Initials = initials.toUpperCase();

if(Inputs['Selected Value'] === Inputs.Value) {
    if(Component.ParentObject) Component.ParentObject['Selected Label'] = Inputs.Label
    Component.Object.State = 'Selected'
}
else {
    Component.Object.State ='Unselected';
}

if(Inputs.Enabled === false)
    Outputs.State = "Disabled"
else if(Inputs.Selected)
    Outputs.State = "Pressed"    
else if(Inputs.Hover)
    Outputs.State = "Hover"
else
    Outputs.State = "Default"

// Compute the slider position from the current value
Component.Object['Max Value'] = Inputs.MaxValue;
Component.Object['Min Value'] = Inputs.MinValue;

if(Inputs.HandleWidth === undefined || Inputs.SliderWidth === undefined) return;

var value = Component.Object['Current Value']
if(value === undefined) return;

var percentage = (value - Inputs.MinValue) / (Inputs.MaxValue - Inputs.MinValue)

Component.Object['Slider Percentage']  = percentage * 100

var sliderPos = (percentage * (Inputs.SliderWidth - Inputs.HandleWidth))

Component.Object.SliderWidth = Inputs.SliderWidth;
Component.Object.HandleWidth = Inputs.HandleWidth;

Component.Object.SliderPos = sliderPos

// Set current value from slider position

var pos = Component.Object.SliderPos;
var maxValue = Component.Object['Max Value'];
var minValue = Component.Object['Min Value'];

var percentage = pos / (Component.Object.SliderWidth - Component.Object.HandleWidth)

Component.Object['Slider Percentage'] = percentage * 100

var currentValue =  (percentage * (maxValue - minValue)) + minValue

Component.Object['Current Value'] = currentValue

if(Inputs.Enabled === false)
    Component.Object.State = "Disabled"
else if(Inputs['Show Error'])
    Component.Object.State = "Error"
else if(Inputs.Focused)
    Component.Object.State = "Focused"
else if(Inputs.Hover)
    Component.Object.State = "Hover"
else
    Component.Object.State = "Default"

